DROP TABLE IF EXISTS WAREHOUSING.PERSONS;
DROP TABLE IF EXISTS WAREHOUSING.DEAL_DETAILS;

CREATE TABLE `PERSONS` (
  `FIRST_NAME` VARCHAR(50) DEFAULT NULL,
  `LAST_NAME` VARCHAR(45) DEFAULT NULL,
  `EMAIL` VARCHAR(45) DEFAULT NULL,
  `ID` BIGINT(20) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`ID`)
) ENGINE=INNODB AUTO_INCREMENT=3 DEFAULT CHARSET=UTF8;

CREATE TABLE `DEAL_DETAILS` (
  `DEAL_UNIQUE_ID` INT(11) NOT NULL,
  `FROM_CURRENCY_ISO_CODE` VARCHAR(45) NOT NULL,
  `TO_CURRENCY_ISO_CODE` VARCHAR(45) NOT NULL,
  `DEAL_TIMESTAMP` VARCHAR(45) NOT NULL,
  `DEAL_AMOUNT` VARCHAR(45) NOT NULL,
  PRIMARY KEY (`DEAL_UNIQUE_ID`)
) ENGINE=INNODB DEFAULT CHARSET=UTF8;