DROP TABLE IF EXISTS WAREHOUSING.DEAL_DETAILS;
DROP TABLE IF EXISTS WAREHOUSING.INVALID_DEALS;

CREATE TABLE `DEAL_DETAILS` (
  `DEAL_UNIQUE_ID` INT(11) NOT NULL,
  `FROM_CURRENCY_ISO_CODE` VARCHAR(3) NOT NULL,
  `TO_CURRENCY_ISO_CODE` VARCHAR(3) NOT NULL,
  `DEAL_TIMESTAMP` VARCHAR(45) NOT NULL,
  `DEAL_AMOUNT` VARCHAR(45) NOT NULL,
  PRIMARY KEY (`DEAL_UNIQUE_ID`)
) ENGINE=INNODB DEFAULT CHARSET=UTF8;

CREATE TABLE `INVALID_DEALS` (
  `DEAL_UNIQUE_ID` INT(11),
  `FROM_CURRENCY_ISO_CODE` VARCHAR(45),
  `TO_CURRENCY_ISO_CODE` VARCHAR(45),
  `DEAL_TIMESTAMP` VARCHAR(45),
  `DEAL_AMOUNT` VARCHAR(45),
  PRIMARY KEY (`DEAL_UNIQUE_ID`)
) ENGINE=INNODB DEFAULT CHARSET=UTF8;